mapper.select:
/* board.selectList */
SELECT SEQ seq , TITLE title , CONT cont , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , F_USER_NM(RGST_ID) name , ATCH_FILE_ID atchFileId , (SELECT COUNT(*) FROM T_FREE_CMT WHERE SUB_SEQ = TB.SEQ AND USE_YN='Y') countCmt FROM T_FREE TB WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* board.insertContents */

			INSERT INTO T_FREE(
           	  	  SEQ 
				, TITLE 
				, CONT
				, RGST_ID 
				, ATCH_FILE_ID 
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.SEQ)+1,1) FROM T_FREE TB)
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
			)

		/* cmt.insertContents */

			INSERT INTO T_FREE_CMT(
           	  	  SUB_SEQ 
				, CONT
				, RGST_ID 
			)
			VALUES (
				 #{subSeq ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
			)

mapper.update:
/* board.updateContents */

	UPDATE T_FREE     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, ATCH_FILE_ID = #{atchFileId ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

/* cmt.updateContents */

	UPDATE T_FREE_CMT     
	 SET 
		 CONT = #{updateCmtCont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
	WHERE  BCMT_SEQ = #{bcmtSeq}

	/* board.deleteContents */

		UPDATE T_FREE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}

	/* cmt.deleteContents */

		UPDATE T_FREE_CMT
	       SET
          	 USE_YN='N'
		WHERE BCMT_SEQ = #{bcmtSeq}
