mapper.select:
/* logmanage.selectList */
SELECT SEQ seq , USERKIND userkind , ID id , DATE_FORMAT(RGST_DT,'%Y.%m.%d %T') rgstDt , RGST_ID rgstId , F_USER_NM(ID) name , URL url , IP ip FROM T_LOGMANAGE TB WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.selectList */
SELECT TB.SEQ , TB.ID , (SELECT NAME FROM T_AD_USER WHERE ID = TB.ID) name , (SELECT IP FROM t_login_fail WHERE ID = TB.ID ORDER BY RGST_DT DESC LIMIT 1) ip , (SELECT MAX(RGST_DT) FROM T_LOGIN_FAIL WHERE ID = TB.ID) rgstDt , COUNT(*) loginFailCount FROM T_LOGIN_FAIL as TB JOIN T_AD_USER as TU ON TU.ID = TB.ID WHERE TB.USE_YN='Y'
GROUP BY TB.ID
ORDER BY TB.SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* T_BANNEDIP_CONATTEMP.selectList */
SELECT SEQ seq , DATE_FORMAT(RGST_DT,'%Y.%m.%d %T') rgstDt , IP ip FROM T_BANNEDIP_CONATTEMP TB WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.multipleConAttemp.selectList */
SELECT SEQ seq , USERKIND userkind , ID id , DATE_FORMAT(RGST_DT,'%Y.%m.%d %H:%i') rgstDt , F_USER_NM(ID) name , URL url , IP ip , count(*) multipleConCount FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY ID, URL, DATE_FORMAT(RGST_DT,'%Y.%m.%d %H:%i')
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.selectCount */ SELECT COUNT(*) cnt FROM ( SELECT SEQ , count(*) multipleConCount FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY ID, URL, DATE_FORMAT(RGST_DT,'%Y.%m.%d %H:%i')
) as T1

/* logmanage.selectCount */ SELECT COUNT(*) cnt FROM ( SELECT COUNT(*) loginFailCount FROM T_LOGIN_FAIL as TB JOIN T_AD_USER as TU ON TU.ID = TB.ID WHERE TB.USE_YN='Y'
GROUP BY TB.ID
) as T1

mapper.insert:
		/* logmanage.insertContents */

			INSERT INTO T_LOGMANAGE(
           	  	  SEQ 
				, USERKIND 
				, ID
				, RGST_ID 
				, URL
				, IP
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.SEQ)+1,1) FROM T_LOGMANAGE TB)
				,#{userkind ,jdbcType=VARCHAR}
				,#{id ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{url ,jdbcType=VARCHAR}
				,#{ip ,jdbcType=VARCHAR}
			)

		/* logmanage.insertContents */

			INSERT INTO T_LOGIN_FAIL(
           	  	  SEQ 
				, USERKIND 
				, ID
				, IP
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.SEQ)+1,1) FROM T_LOGIN_FAIL TB)
				,#{userkind ,jdbcType=VARCHAR}
				,#{id ,jdbcType=VARCHAR}
				,#{ip ,jdbcType=VARCHAR}
			)

mapper.update:
/* logmanage.updateContents */

	UPDATE T_LOGMANAGE     
	 SET 
		  USERKIND = #{userkind ,jdbcType=VARCHAR} 
		, ID = #{id ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, URL = #{url ,jdbcType=VARCHAR}
		, IP = #{ip ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* logmanage.deleteContents */

		UPDATE T_LOGMANAGE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
