mapper.select:
/* logmanage.selectList */
SELECT MAX(SEQ) seq , USERKIND userkind , ID id , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , MAX(RGST_ID) rgstId , F_USER_NM(ID) name , URL url , IP ip FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY TB.USERKIND,TB.ID,DATE_FORMAT(TB.RGST_DT,'%Y.%m.%d'),TB.URL,TB.IP ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.selectList */
SELECT MAX(TB.SEQ) seq , (SELECT ID FROM T_LOGIN_FAIL WHERE IP = TB.IP ORDER BY RGST_DT DESC LIMIT 1) id , F_USER_NM(TB.ID) name , TB.IP ip , MAX(TB.RGST_DT) rgstDt , COUNT(*) loginFailCount , CASE WHEN COUNT(*) > 4 THEN 'Y' ELSE 'N' END banned FROM T_LOGIN_FAIL as TB WHERE TB.USE_YN='Y'
GROUP BY TB.IP, DATE(TB.RGST_DT)
ORDER BY TB.SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* T_BANNEDIP_CONATTEMP.selectList */
SELECT SEQ seq , DATE_FORMAT(RGST_DT,'%Y.%m.%d %T') rgstDt , IP ip FROM T_BANNEDIP_CONATTEMP TB WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.multipleConAttemp.selectList */
SELECT MAX(SEQ) seq , MAX(USERKIND) userkind , ID id , MAX(DATE_FORMAT(RGST_DT,'%Y.%m.%d')) rgstDt , F_USER_NM(ID) name , URL url , IP ip , count(*) multipleConCount FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY ID, URL, DATE_FORMAT(RGST_DT,'%Y.%m.%d') ,IP
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* logmanage.selectCount */ SELECT COUNT(*) cnt FROM ( SELECT MAX(SEQ) , count(*) multipleConCount FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY ID, URL, DATE_FORMAT(RGST_DT,'%Y.%m.%d') ,IP
) as T1

/* logmanage.selectCount */ SELECT COUNT(*) cnt FROM ( SELECT COUNT(*) loginFailCount FROM T_LOGIN_FAIL as TB WHERE TB.USE_YN='Y'
GROUP BY TB.IP, DATE(TB.RGST_DT)
) as T1

/* logmanage.selectCount */ SELECT COUNT(*) cnt FROM (
SELECT MAX(TB.SEQ) seq FROM T_LOGMANAGE TB WHERE USE_YN='Y'
GROUP BY TB.USERKIND,TB.ID,DATE_FORMAT(TB.RGST_DT,'%Y.%m.%d'),TB.URL,TB.IP ) as T1

mapper.insert:
		/* logmanage.insertContents */

			INSERT INTO T_LOGMANAGE(
           	  	  SEQ 
				, USERKIND 
				, ID
				, RGST_ID 
				, URL
				, IP
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.SEQ)+1,1) FROM T_LOGMANAGE TB)
				,#{userkind ,jdbcType=VARCHAR}
				,#{id ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{url ,jdbcType=VARCHAR}
				,#{ip ,jdbcType=VARCHAR}
			)

		/* logmanage.insertContents */

			INSERT INTO T_LOGIN_FAIL(
           	  	  SEQ 
				, USERKIND 
				, ID
				, IP
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.SEQ)+1,1) FROM T_LOGIN_FAIL TB)
				,#{userkind ,jdbcType=VARCHAR}
				,#{id ,jdbcType=VARCHAR}
				,#{ip ,jdbcType=VARCHAR}
			)

mapper.update:
/* logmanage.updateContents */

	UPDATE T_LOGMANAGE     
	 SET 
		  USERKIND = #{userkind ,jdbcType=VARCHAR} 
		, ID = #{id ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, URL = #{url ,jdbcType=VARCHAR}
		, IP = #{ip ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

/* logmanage.updateContents */
            UPDATE T_LOGIN_FAIL
            SET
            	USE_YN = 'N' 
            WHERE IP = #{ip ,jdbcType=VARCHAR};

	/* logmanage.deleteContents */

		UPDATE T_LOGMANAGE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
