mapper.select:
/* mmain.selectList */
SELECT SEQ seq ,MAIN_TITLE mainTitle ,MAIN_SUBTITLE mainSubtitle ,MAIN_ADDR mainAddr ,MAIN_HP mainHp ,MAIN_FAX mainFax ,RPRSNTR rprsntr ,RGST_ID rgstId ,RVSE_DT rvseDt ,RVSE_ID rvseId ,USE_YN useYn ,BG_IMG bgImg ,M_BG_IMG mBgImg ,M_AD_BOARD1_SEQ mAdBoard1Seq ,M_AD_BOARD2_SEQ mAdBoard2Seq ,M_AD_BOARD3_SEQ mAdBoard3Seq ,M_AD_BOARD4_SEQ mAdBoard4Seq , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt FROM T_MMAIN TS WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* mainbanner.selectList */
SELECT SEQ seq ,MAIN_TITLE mainTitle ,MAIN_SUBTITLE mainSubtitle ,MAIN_ADDR mainAddr ,MAIN_HP mainHp ,MAIN_FAX mainFax ,RPRSNTR rprsntr ,RGST_ID rgstId ,RVSE_DT rvseDt ,RVSE_ID rvseId ,USE_YN useYn ,BG_IMG bgImg ,M_BG_IMG mBgImg ,M_AD_BOARD1_SEQ mAdBoard1Seq ,M_AD_BOARD2_SEQ mAdBoard2Seq ,M_AD_BOARD3_SEQ mAdBoard3Seq ,M_AD_BOARD4_SEQ mAdBoard4Seq , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt FROM T_MMAIN_BANNER WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* mmain.insertContents */

			INSERT INTO T_MMAIN(
           	  	  SEQ 
				, TITLE 
				, CONT
				, RGST_ID 
				, ATCH_FILE_ID 
				, STA_DATE
				, END_DATE 
				, NOTI_YN 
			)
			VALUES (
				 (SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_MMAIN TS)
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
				,#{staDate ,jdbcType=VARCHAR}
				,#{endDate ,jdbcType=VARCHAR}
				,#{notiYn ,jdbcType=VARCHAR}
			)

mapper.update:
/* mmain.updateContents */

	UPDATE T_MMAIN     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, ATCH_FILE_ID = #{atchFileId ,jdbcType=VARCHAR}
		, STA_DATE = #{staDate ,jdbcType=VARCHAR}
		, END_DATE  = #{endDate ,jdbcType=VARCHAR}
		, NOTI_YN  = #{notiYn ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* mmain.deleteContents */

		UPDATE T_MMAIN
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
