mapper.select:
/* Sample.selectList */
SELECT SEQ seq , TITLE title , CONT cont , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , F_USER_NM(RGST_ID) name , (SELECT COUNT(*) FROM T_MAIL_VISITIP WHERE MAIL_SEQ = TS.SEQ) visitedCnt FROM T_MAIL TS WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* Sample.selectList */
SELECT SEQ seq , TITLE title , CONT cont , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , F_USER_NM(RGST_ID) name FROM T_MAIL_R WHERE USE_YN='Y' AND MAIL_SEQ = #{seq,jdbcType=VARCHAR}
ORDER BY SEQ DESC

/* Sample.selectList */
SELECT SEQ seq , EMAIL email , ID id , NAME name FROM T_AD_USER WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* Sample.selectList */
SELECT COUNT(SEQ) cnt FROM T_AD_USER WHERE USE_YN='Y'
ORDER BY SEQ DESC

mapper.insert:
		/* Sample.insertContents */

			INSERT INTO T_MAIL(
           	  	  SEQ 
				, TITLE 
				, CONT
				, RGST_ID 
			)
			VALUES (
				 (SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_MAIL TS)
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
			)

/* mmain.insertContents */
	INSERT INTO T_MAIL_R(
		SEQ
		,MAIL_SEQ
		, R_EMAIL
	)
	VALUES 

	(	
		(SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_MAIL_R TS)+#{index}+1
		,#{item.mailSeq ,jdbcType=VARCHAR}
		,#{item.rEmail ,jdbcType=VARCHAR}
	)

/* mmain.insertContents */
	INSERT INTO T_MAIL_R(
		SEQ
		,MAIL_SEQ
		, R_EMAIL
	)
	VALUES 
	(	
		(SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_MAIL_R TS)
		,#{item.mailSeq ,jdbcType=VARCHAR}
		,#{item.rEmail ,jdbcType=VARCHAR}
	)

mapper.update:
/* Sample.updateContents */

	UPDATE T_MAIL     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
	WHERE  SEQ = #{seq}

/* Sample.updateContents */

	UPDATE T_MAIL_R     
	 SET 
		  R_EMAIL = #{rEmail ,jdbcType=VARCHAR} 
		, RVSE_DT = NOW()
		, USE_YN = 'Y'
	WHERE  SEQ = #{seq}

	/* Sample.deleteContents */

		UPDATE T_MAIL
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}

	/* Sample.deleteContents */

		UPDATE T_MAIL_R
	       SET
          	 USE_YN='N'
		WHERE MAIL_SEQ=#{seq}
