mapper.select:
/* Sample.selectList */
SELECT SEQ seq , TITLE title , CONT cont , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , F_USER_NM(RGST_ID) name , ATCH_FILE_ID atchFileId , STA_DATE staDate , END_DATE endDate FROM T_SAMPLE TS WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
/* testexcel.insertContents */
	INSERT INTO T_TESTEXCEL(
		 TEST_COL1
		, TEST_COL2
		, TEST_COL3
		, TEST_COL4
		, TEST_COL5
		, IMG_COL1
		, EXCEL_ATCH_FILE_ID

	)
	VALUES 

			(
			#{item.testCol1}
			,#{item.testCol2}
			,#{item.testCol3}
			,#{item.testCol4}
			,#{item.testCol5}
			,#{item.imgCol1}
			,#{atchFileId}
			)

mapper.update:
/* Sample.updateContents */

	UPDATE T_SAMPLE     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, ATCH_FILE_ID = #{atchFileId ,jdbcType=VARCHAR}
		, STA_DATE = #{staDate ,jdbcType=VARCHAR}
		, END_DATE  = #{endDate ,jdbcType=VARCHAR}
		, NOTI_YN  = #{notiYn ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* Sample.deleteContents */

		UPDATE T_SAMPLE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
