mapper.select:
/* mmain.selectList */
SELECT SEQ seq ,MAIN_TITLE mainTitle ,MAIN_SUBTITLE mainSubtitle ,MAIN_ADDR mainAddr ,MAIN_HP mainHp ,MAIN_FAX mainFax ,RPRSNTR rprsntr ,RGST_ID rgstId ,RVSE_DT rvseDt ,RVSE_ID rvseId ,USE_YN useYn ,BG_IMG bgImg ,M_BG_IMG mBgImg ,M_AD_BOARD1_SEQ mAdBoard1Seq ,M_AD_BOARD2_SEQ mAdBoard2Seq ,M_AD_BOARD3_SEQ mAdBoard3Seq ,M_AD_BOARD4_SEQ mAdBoard4Seq , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt FROM T_MMAIN TS WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* mmain.selectList */
SELECT SEQ seq , MMAIN_SEQ mmainSeq , BANNER_TITLE bannerTitle , URL url , NOTI_YN notiYn , RGST_ID rgstId , RVSE_DT rvseDt , RVSE_ID rvseId , USE_YN useYn , DATE(STA_DATE) staDate , DATE(END_DATE) endDate , IORDER iorder , NEWTAB newtab , ATCH_FILE_ID atchFileId , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt FROM T_MMAIN_BANNER WHERE USE_YN='Y' AND MMAIN_SEQ = #{seq ,jdbcType=VARCHAR}
ORDER BY IORDER

