mapper.select:
/* Sample.selectList */
SELECT COM com ,ADDRESS address ,AREA area ,VISIT_DATE visitDate FROM v_complain_v2 WHERE 1=1
AND DATE_FORMAT(VISIT_DATE, '%Y') = #{schEtc02}
ORDER BY VISIT_DATE DESC LIMIT 4

/* Sample.selectList */
SELECT * ,(SELECT area1+area2+area3+area4+area5+area6) total FROM ( SELECT area1,area2,area3,area4,area5,area6 ,DATE_FORMAT(VISIT_DATE,'%Y') visitDate FROM v_complain_total WHERE 1=1
AND DATE_FORMAT(VISIT_DATE, '%Y') = #{schEtc02}
ORDER BY VISIT_DATE DESC LIMIT 1 ) t1

mapper.insert:
		/* Sample.insertContents */

			INSERT INTO T_SAMPLE(
           	  	  SEQ 
				, TITLE 
				, CONT
				, RGST_ID 
				, ATCH_FILE_ID 
				, STA_DATE
				, END_DATE 
				, NOTI_YN 
			)
			VALUES (
				 (SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_SAMPLE TS)
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
				,#{staDate ,jdbcType=VARCHAR}
				,#{endDate ,jdbcType=VARCHAR}
				,#{notiYn ,jdbcType=VARCHAR}
			)

mapper.update:
/* Sample.updateContents */

	UPDATE T_SAMPLE     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, ATCH_FILE_ID = #{atchFileId ,jdbcType=VARCHAR}
		, STA_DATE = #{staDate ,jdbcType=VARCHAR}
		, END_DATE  = #{endDate ,jdbcType=VARCHAR}
		, NOTI_YN  = #{notiYn ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* Sample.deleteContents */

		UPDATE T_SAMPLE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
